import { init_logging as main_init_logging } from '../core/util/logging.js';

// init log level reading the logging HTTP param
export function init_logging(level) {
    "use strict";
    if (typeof level !== "undefined") {
        main_init_logging(level);
    } else {
	const varString = atob( document.location.href.match( /token=([A-Za-z0-9._-]*)/ )[ 0 ].substring( 6 ) );
        const param = varString.match(/logging=([A-Za-z0-9._-]*)/);
        main_init_logging(param || undefined);
    }
}

// Read a query string variable
export function getQueryVar(name, defVal) {
    "use strict";
    const varString = atob( document.location.href.match( /token=([A-Za-z0-9._-]*)/ )[ 0 ].substring( 6 ) );
    const re = new RegExp('.*[?&]' + name + '=([^&#]*)'),
        match = varString.match(re);
    if (typeof defVal === 'undefined') { defVal = null; }

    if (match) {
        return decodeURIComponent(match[1]);
    }

    return defVal;
}

// Read a hash fragment variable
export function getHashVar(name, defVal) {
    "use strict";
    const re = new RegExp('.*[&#]' + name + '=([^&]*)'),
        match = document.location.hash.match(re);
    if (typeof defVal === 'undefined') { defVal = null; }

    if (match) {
        return decodeURIComponent(match[1]);
    }

    return defVal;
}

// Read a variable from the fragment or the query string
// Fragment takes precedence
export function getConfigVar(name, defVal) {
    "use strict";
    const val = getHashVar(name);

    if (val === null) {
        return getQueryVar(name, defVal);
    }

    return val;
}

